-- 刪DB
DROP DATABASE IF EXISTS allieatfinal_db01;

-- 選DB
CREATE DATABASE IF NOT EXISTS allieatfinal_db01;
USE allieatfinal_db01;

-- 以下為表格建立-------------------------------------

-- 後台----------------------------------------------
CREATE TABLE administrator (
                               adminId     INT PRIMARY KEY AUTO_INCREMENT,
                               account     VARCHAR(10) NOT NULL,
    -- 移除不合法的 DEFAULT (CONCAT('pw',account))
                               password    VARCHAR(255) NOT NULL,
                               createdTime TIMESTAMP NOT NULL
                                   DEFAULT CURRENT_TIMESTAMP
                                   ON UPDATE CURRENT_TIMESTAMP
);


-- 點數發放規則 ----------------------------------------------
CREATE TABLE payrule (
                         payRuleId   INT PRIMARY KEY AUTO_INCREMENT,
                         payoutDay   INT NOT NULL,
                         payoutTime  TIME NOT NULL,
                         payoutPoints INT NOT NULL,
                         updatedAt   TIMESTAMP
                             DEFAULT CURRENT_TIMESTAMP
                             ON UPDATE CURRENT_TIMESTAMP
);

-- 捐款紀錄
CREATE TABLE DONATIONRECORD (
                                donationRecordId INT PRIMARY KEY AUTO_INCREMENT,
                                createdTime      TIMESTAMP
                                                                                    DEFAULT CURRENT_TIMESTAMP
                                    ON UPDATE CURRENT_TIMESTAMP,
                                identityData     VARCHAR(255) NOT NULL,
                                donationIncome   INT NOT NULL,
                                email            VARCHAR(50) NOT NULL,
                                phone            VARCHAR(10) NOT NULL,
                                bthDate          VARCHAR(20),
                                gender           INT,
                                county           VARCHAR(20),
                                district         VARCHAR(255),
                                postalCode       INT,
                                address          VARCHAR(255),
                                mailMtd          TINYINT NOT NULL,
                                salutation       VARCHAR(90),
                                idNum            VARCHAR(50),
                                guiNum           VARCHAR(8),
                                anonymous        TINYINT,
                                merchantTradeNo  VARCHAR(50) COMMENT '綠界訂單編號' DEFAULT NULL,
                                donationType     TINYINT NOT NULL,
                                paidStatus       TINYINT
);

-- 捐款總額----------------------------------------
CREATE TABLE totalAmount (
                             totalAmountId INT PRIMARY KEY AUTO_INCREMENT COMMENT '捐款總額ID',
                             totalAmount   INT NOT NULL COMMENT '捐款總額',
                             updatedTime   TIMESTAMP NOT NULL
                                 DEFAULT CURRENT_TIMESTAMP
                                 ON UPDATE CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='捐款總額表';

-- 捐款總額--------------------------------------------------
CREATE TABLE donation (
                          rankId      INT AUTO_INCREMENT PRIMARY KEY,
                          amount      INT,
                          updatedTime TIMESTAMP
                              DEFAULT CURRENT_TIMESTAMP
                              ON UPDATE CURRENT_TIMESTAMP
);

-- 捐款排行----------------------------------------------
CREATE TABLE rankrank (
                          rankId        INT PRIMARY KEY AUTO_INCREMENT COMMENT '捐款排行ID',
                          identityData  VARCHAR(255) NOT NULL COMMENT '姓名/公司/機構名稱',
                          totalDonation INT NOT NULL COMMENT '累積捐款金額',
                          updatedTime   TIMESTAMP NOT NULL
                              DEFAULT CURRENT_TIMESTAMP
                              ON UPDATE CURRENT_TIMESTAMP COMMENT '最後更新時間'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='捐款排行表';

-- 點數發放----------------------------------------------------
DROP TABLE IF EXISTS payRecord;

CREATE TABLE payRecord (
                           payoutId     INT PRIMARY KEY AUTO_INCREMENT,
                           payRuleId    INT,
                           payoutPoints INT NOT NULL,
                           totalPoints  INT,
                           scheduleTime TIMESTAMP NOT NULL,
                           payoutDate   TIMESTAMP,
                           status       TINYINT NOT NULL,
                           CONSTRAINT payRecord_payRuleId_FK
                               FOREIGN KEY (payRuleId) REFERENCES payrule (payRuleId)
                                   ON DELETE SET NULL
);

-- 在冊單位------------------------------------------
CREATE TABLE organization (
                              organizationId INT PRIMARY KEY AUTO_INCREMENT,
                              name           VARCHAR(255) NOT NULL,
                              type           VARCHAR(50) NOT NULL,
                              createdTime    TIMESTAMP NOT NULL
                                  DEFAULT CURRENT_TIMESTAMP
                                  ON UPDATE CURRENT_TIMESTAMP,
                              status         TINYINT NOT NULL
);

-- 受助者---------------------------
CREATE TABLE MEMBER (
                        memberId           INT PRIMARY KEY AUTO_INCREMENT,
                        organizationId     INT,
                        name               VARCHAR(255) NOT NULL,
                        idNum              VARCHAR(50) NOT NULL,
                        permAddr           VARCHAR(255) NOT NULL,
                        address            VARCHAR(255) NOT NULL,
                        regTime            TIMESTAMP NOT NULL
                                                        DEFAULT CURRENT_TIMESTAMP
                            ON UPDATE CURRENT_TIMESTAMP,
                        kycImage           VARCHAR(255) NOT NULL,
                        email              VARCHAR(100) NOT NULL,
                        phone              VARCHAR(20) NOT NULL,
                        account            VARCHAR(50) NOT NULL,
                        password           VARCHAR(50) NOT NULL,
                        pointsBalance      INT NOT NULL,
                        unclaimedMealCount INT,
                        accStat            INT NOT NULL,
                        reviewed           TINYINT NOT NULL,
                        verificationMail   VARCHAR(255) DEFAULT NULL,
                        CONSTRAINT member_organizationId_FK
                            FOREIGN KEY (organizationId) REFERENCES organization (organizationId)
                                ON DELETE SET NULL
);

-- 點數發放明細 --------------------------------------------
CREATE TABLE payDetail (
                           payDetailId   INT PRIMARY KEY AUTO_INCREMENT,
                           memberId      INT,
                           payoutId      INT,
                           pointsExpensed INT NOT NULL,
                           createdTime   TIMESTAMP
                               DEFAULT CURRENT_TIMESTAMP
                               ON UPDATE CURRENT_TIMESTAMP,
                           CONSTRAINT paydetail_memberId_FK
                               FOREIGN KEY (memberId) REFERENCES MEMBER (memberId)
                                   ON DELETE SET NULL,
                           CONSTRAINT paydetail_payoutId_FK
                               FOREIGN KEY (payoutId) REFERENCES payRecord (payoutId)
                                   ON DELETE SET NULL
);

-- 店家------------------------------------------
CREATE TABLE store (
                       storeId          INT NOT NULL AUTO_INCREMENT,
                       name             VARCHAR(255) NOT NULL,
                       managerName      VARCHAR(50) NOT NULL,
                       phoneNum         VARCHAR(20) NOT NULL,
                       email            VARCHAR(50) NOT NULL,
                       password         VARCHAR(50) NOT NULL,
                       guiNum           VARCHAR(8) NOT NULL,
                       businessRegNum   VARCHAR(20) NOT NULL,
                       reviewed         TINYINT NOT NULL,
                       regTime          TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
                       points           INT NOT NULL,
                       accStat          TINYINT NOT NULL,
                       opStat           TINYINT NOT NULL,
                       opTime           VARCHAR(50),
                       pickTime         VARCHAR(50),
                       lastOrder        VARCHAR(50),
                       closeTime        VARCHAR(50),
                       address          VARCHAR(90) NOT NULL,
                       county           VARCHAR(255) NOT NULL,
                       district         VARCHAR(255) NOT NULL,
                       postalCode       INT NOT NULL,
                       starNum          INT NOT NULL,
                       visitorsNum      INT NOT NULL,
                       mapApi           VARCHAR(255),
                       verificationMail VARCHAR(255) DEFAULT NULL,
                       CONSTRAINT STORE_PRIMARY_KEY PRIMARY KEY (storeId)
);

-- 飲食分類--------------------------------
CREATE TABLE foodType (
                          foodTypeId INT AUTO_INCREMENT,
                          storeId    INT,
                          type       VARCHAR(255) NOT NULL,
                          CONSTRAINT foodType_PRIMARY_KEY PRIMARY KEY (foodTypeId),
                          FOREIGN KEY (storeId) REFERENCES store (storeId)
                              ON DELETE SET NULL
);

-- 店家照片---------------------------------
CREATE TABLE photo (
                       photoId    INT NOT NULL AUTO_INCREMENT,
                       storeId    INT,
                       photoSrc   LONGBLOB,
                       photoType  VARCHAR(255),
                       updateTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
                       CONSTRAINT photo_PRIMARY_KEY PRIMARY KEY (photoId),
                       FOREIGN KEY (storeId) REFERENCES store (storeId)
                           ON DELETE SET NULL
);

-- 餐點-------------------------
CREATE TABLE food (
                      foodId      INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
                      storeId     INT,
                      name        VARCHAR(255) NOT NULL,
                      createdTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
                      status      TINYINT NOT NULL,
                      amount      INT NOT NULL,
                      photo       VARCHAR(255) NOT NULL,
                      cost        INT NOT NULL,
                      CONSTRAINT food_storeId_FK
                          FOREIGN KEY (storeId) REFERENCES store (storeId)
                              ON DELETE SET NULL
);

-- 附餐------------------
CREATE TABLE attached (
                          attachedId  INT PRIMARY KEY AUTO_INCREMENT,
                          foodId      INT,
                          name        VARCHAR(255) NOT NULL,
                          createdTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
                          CONSTRAINT attached_foodId_FK
                              FOREIGN KEY (foodId) REFERENCES food (foodId)
                                  ON DELETE SET NULL
);

-- 訂單-------------------------
CREATE TABLE orderList (
                           orderId     INT PRIMARY KEY AUTO_INCREMENT,
                           storeId     INT,
                           memberId    INT,
                           rate        TINYINT,
                           comment     VARCHAR(255),
                           serveStat   TINYINT NOT NULL,
                           pickStat    TINYINT NOT NULL,
                           pickTime    TIMESTAMP
                                                 DEFAULT CURRENT_TIMESTAMP
                               ON UPDATE CURRENT_TIMESTAMP,
                           createdTime TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                           CONSTRAINT orderfood_memberId_FK
                               FOREIGN KEY (memberId) REFERENCES MEMBER (memberId)
                                   ON DELETE SET NULL,
                           CONSTRAINT orderfood_storeId_FK
                               FOREIGN KEY (storeId) REFERENCES store (storeId)
                                   ON DELETE SET NULL
);

-- 注意：table 名叫 orderList（L 大寫），所以 ALTER TABLE 要用一樣的大小寫
ALTER TABLE orderList
    ADD COLUMN fcm_token VARCHAR(512) NULL;

-- 訂單明細--------------------------------------------
CREATE TABLE ORDERDETAIL (
                             orderId    INT NOT NULL,
                             foodId     INT NOT NULL,
                             createdTime TIMESTAMP NOT NULL
                                 DEFAULT CURRENT_TIMESTAMP
                                 ON UPDATE CURRENT_TIMESTAMP,
                             amount     INT NOT NULL,
                             pointsCost INT NOT NULL,
                             note       VARCHAR(100),
                             PRIMARY KEY (orderId, foodId),
                             CONSTRAINT orderdetail_orderId_FK
                                 FOREIGN KEY (orderId) REFERENCES orderList (orderId)
                                     ON DELETE CASCADE,
                             CONSTRAINT orderdetail_foodId_FK
                                 FOREIGN KEY (foodId) REFERENCES food (foodId)
                                     ON DELETE CASCADE
);

-- 點數轉換金額記錄表 --------------------------------------------
CREATE TABLE pointsRedemption (
                                  redemptionId   INT PRIMARY KEY AUTO_INCREMENT,
                                  storeId        INT,
                                  redemptionMonth DATE NOT NULL,
                                  pointsAmount   INT NOT NULL,
                                  cashAmount     INT,
                                  status         TINYINT NOT NULL,
                                  paymentTime    TIMESTAMP NULL,
                                  createdTime    TIMESTAMP NULL DEFAULT CURRENT_TIMESTAMP,
                                  CONSTRAINT pointsredemption_storeid_FK
                                      FOREIGN KEY (storeId) REFERENCES store (storeId)
                                          ON DELETE SET NULL
);


